import { useState } from 'react';

import './App.css';

import InputField from './components/InputField';

function App() {

	const [formFields, setFormFields] = useState(
		{
			username: '',
			password: '',
			confirmPassword: ''
		}
	);

	const handleFieldChange = (name, val) => {
		setFormFields(prevState => {
			return {
				...prevState,
				[name]: val
			}
		});
	};

	return (

		<div className='container'>

			<h1>TST Signup Form</h1>

			<form className='tst-form'>

				<InputField
					changeHandler={(e) => handleFieldChange('username', e.target.value)}
					label='Username'
					name='username'
					value={formFields.username}
				/>

				<InputField
					changeHandler={(e) => handleFieldChange('password', e.target.value)}
					label='Password'
					name='password'
					type='password'
					value={formFields.password}
				/>

				<InputField
					changeHandler={(e) => handleFieldChange('confirmPassword', e.target.value)}
					label='Confirm Password'
					name='confirmPassword'
					type='password'
					value={formFields.confirmPassword}
				/>

			</form>

		</div>

	);

}

export default App;
