import { useEffect, useState } from 'react';

import Button from './Button';

export default function Banner({ text = 'Banner Component' }) {

    const handleButtonClick = (e) => {
        e.preventDefault();
        alert('CLICKED!');
    }

	return (

		<div className='banner'>

            <h1>{text}</h1>

            <Button
                label='Click Me!'
                onClick={handleButtonClick}
            />

        </div>

	)

}
