import { useEffect, useState } from 'React';

import Button from './Button';

export default function Banner({ img, text = 'Image Banner' }) {

	return (

		<div className='fm-banner text-center'>

            {mobileImg && (
                <img
                    className='fm-banner__img fm-banner__img--sm img-fluid d-sm-none'
                    src={mobileImg}
                />
            )}

            <img
                className={`fm-banner__img fm-banner__img--lg img-fluid${mobileImg ? ' d-none d-sm-inline' : ''}`}
                src={img}
            />

            <span className='visually-hidden'>{text}</span>
        </div>

	)

}
