import { useEffect, useState } from 'react';

import Button from './Button';

export default function Banner({ text = 'Banner Component' }) {

    const handleButtonClick = () => {
        alert('CLICKED!');
    }

    const bannerBtnProps = {
        label: 'Click Me!',
        onClick: handleButtonClick
    }

	return (

		<div className='banner d-flex justify-content-center'>

            <div className='banner__inner d-flex flex-column'>
                <h1>{text}</h1>

                <Button
                    {...bannerBtnProps}
                />
            </div>



        </div>

	)

}
